                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                required
                /> 
            </div>
            </form>
          </div>
          <button className="Reg-button" onClick={handleSubmit}> Login </button>
      </body>
    </div>

      

       



      
        
    
  );
}

export default Login;
